// R√©cup√©rer les donn√©es
const parcelInfo = $('Build Parcel Info').first().json;
const ignZoneUrba = $('IGN Zone Urba').first().json;
const perplexityResponse = $input.first().json;

// D√©terminer le type (RNU ou PLU)
const isRNU = ignZoneUrba.totalFeatures === 0;
const zonage = isRNU ? 'RNU' : (ignZoneUrba.features[0]?.properties?.libelle || 'Inconnu');

// Construire la r√©ponse structur√©e
const response = {
  success: true,
  data: {
    parcelle: {
      commune: parcelInfo.commune,
      section: parcelInfo.section,
      numero: parcelInfo.numero,
      surface: parcelInfo.surface,
      url_geoportail: parcelInfo.url_parcelle
    },
    zonage: {
      type: isRNU ? 'RNU' : 'PLU',
      libelle: zonage,
      url_document: isRNU ? null : `https://data.geopf.fr/annexes/gpu/documents/${ignZoneUrba.features[0].properties.partition}/${ignZoneUrba.features[0].properties.gpu_doc_id}/${ignZoneUrba.features[0].properties.nomfic}`
    },
    analyse: {
      texte: perplexityResponse.choices[0].message.content,
      source: 'Perplexity AI'
    },
    timestamp: new Date().toISOString()
  }
};

return response;
